import React, { useState, useEffect } from 'react';
import service from './service';  // וודא שיבוא גם מקובץ השירות שלך

function App() {
  const [newTodo, setNewTodo] = useState("");
  const [todoId, setTodoId] = useState("");
  const [todos, setTodos] = useState([]);
  const [action, setAction] = useState("add"); // פעולה נבחרת (הוספה, עדכון, מחיקה)
  const [isComplete, setIsComplete] = useState(false); // עבור עדכון מצב משימה

  // פונקציה להחזרת כל המשימות
  async function getTodos() {
    const todos = await service.getTasks();
    setTodos(todos);
  }

  // פונקציה להוספת משימה
  async function createTodo(e) {
    e.preventDefault();
    if (!newTodo) return;
    await service.addTask(newTodo);
    setNewTodo(""); // לנקות את השדה
    await getTodos(); // רענן את רשימת המשימות
  }

  // פונקציה לעדכון מצב משימה
  async function updateCompleted(e) {
    await service.setCompleted(todoId, isComplete);
    await getTodos(); // רענן את רשימת המשימות
  }

  // פונקציה למחיקת משימה
  async function deleteTodo() {
    await service.deleteTask(todoId);
    await getTodos(); // רענן את רשימת המשימות
  }

  // עדכון פעולה
  const handleActionChange = (e) => {
    setAction(e.target.value);
    setTodoId("");
    setNewTodo("");
    setIsComplete(false);
  };

  useEffect(() => {
    getTodos(); // טען את המשימות כשמטעינים את הדף
  }, []);

  return (
    <div className="todoapp">
      <header className="header">
        <h1>todos</h1>
        {/* טופס בחירת פעולה */}
        <form>
          <select onChange={handleActionChange} value={action}>
            <option value="add">הוסף משימה</option>
            <option value="update">עדכן משימה</option>
            <option value="delete">מחק משימה</option>
          </select>
        </form>

        {/* טופס יצירת משימה */}
        {action === "add" && (
          <form onSubmit={createTodo}>
            <input
              className="new-todo"
              placeholder="הוסף משימה"
              value={newTodo}
              onChange={(e) => setNewTodo(e.target.value)}
            />
            <button type="submit">הוסף</button>
          </form>
        )}

        {/* טופס עדכון משימה */}
        {action === "update" && (
          <form onSubmit={updateCompleted}>
            <input
              type="text"
              placeholder="הזן מזהה משימה לעדכון"
              value={todoId}
              onChange={(e) => setTodoId(e.target.value)}
            />
            <label>
              <input
                type="checkbox"
                checked={isComplete}
                onChange={(e) => setIsComplete(e.target.checked)}
              />
              משימה הושלמה
            </label>
            <button type="submit">עדכן</button>
          </form>
        )}

        {/* טופס מחיקת משימה */}
        {action === "delete" && (
          <form onSubmit={deleteTodo}>
            <input
              type="text"
              placeholder="הזן מזהה משימה למחיקה"
              value={todoId}
              onChange={(e) => setTodoId(e.target.value)}
            />
            <button type="submit">מחק</button>
          </form>
        )}
      </header>

      {/* הצגת כל המשימות */}
      <section className="main">
        <ul className="todo-list">
          {todos.map(todo => (
            <li className={todo.isComplete ? "completed" : ""} key={todo.id}>
              <div className="view">
                <label>{todo.name}</label>
                {todo.isComplete ? <span> - הושלם</span> : <span> - לא הושלם</span>}
              </div>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default App;
